  create policy "Enable delete for users based on user_id"
  on "public"."notifications"
  as permissive
  for delete
  to public
using ((( SELECT auth.uid() AS uid) = user_id));



  create policy "Enable insert for users based on user_id"
  on "public"."notifications"
  as permissive
  for insert
  to public
with check ((( SELECT auth.uid() AS uid) = user_id));



  create policy "Enable users to view their own data only"
  on "public"."notifications"
  as permissive
  for select
  to authenticated
using ((( SELECT auth.uid() AS uid) = user_id));



  create policy "Enable read access for all users"
  on "public"."order_item"
  as permissive
  for select
  to public
using (true);



  create policy "Enable insert for users based on user_id"
  on "public"."orders"
  as permissive
  for insert
  to public
with check ((( SELECT auth.uid() AS uid) = user_id));



  create policy "Enable users to view their own data only"
  on "public"."orders"
  as permissive
  for select
  to authenticated
using ((( SELECT auth.uid() AS uid) = user_id));



  create policy "Enable read access for authenticated"
  on "public"."products"
  as permissive
  for select
  to authenticated
using (true);



  create policy "insert own profile"
  on "public"."profiles"
  as permissive
  for insert
  to authenticated
with check ((( SELECT auth.uid() AS uid) = id));



  create policy "select own or admin"
  on "public"."profiles"
  as permissive
  for select
  to authenticated
using (((( SELECT auth.uid() AS uid) = id) OR public.is_admin()));



  create policy "update own or admin"
  on "public"."profiles"
  as permissive
  for update
  to authenticated
using (((( SELECT auth.uid() AS uid) = id) OR public.is_admin()))
with check (((( SELECT auth.uid() AS uid) = id) OR public.is_admin()));


